package quatro;

public class TesteQuatro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Criando uma variável do tipo inteiro
		int idade = 35;
		// Imprimir a variável na tela
		System.out.println(idade);
		// Criando uma variável do tipo string
		String nome = "Isa Mary";
		// Imprimir a variável na tela
		System.out.println(nome);
		// Imprimir as duas variáveis no mesmo texto.
		System.out.println("Meu nome é " + nome + " e eu tenho " + idade + " anos.");
		
		// Gerando a soma de duas variáveis e armazenando em outra variável
		int num1 = 5;
		int num2 = 6;
		int soma = num1 + num2;
		
		System.out.println("A soma da variável com o valor " + num1 + " mais o valor " + num2 + " é " + soma);
	}

}
